    m_chooser.addOption("Do Nothing Auto", new DoNothingAuto());
    m_chooser.addOption("Only Mobility", new MobilityAuto(swerveDrive));
    m_chooser.addOption("One Cube & Mobility", new OneCubeAuto(swerveDrive, claw, arm, wrist, SwerveDrive.headless));
    m_chooser.addOption("Only Balance", new BalanceTogether(swerveDrive,MoveToRamp.kForward));
    m_chooser.addOption("Two Cube Auto", new TwoCubeAuto(swerveDrive, arm, wrist, claw));
    m_chooser.addOption("One Cone one Cube", new OneConeOneCubeAuto(swerveDrive, arm, wrist, claw));
    m_chooser.addOption("One Cube & Balance", new AllAuto(swerveDrive, claw, arm, wrist, true ));
    
    SmartDashboard.putData("Auto Mode", m_chooser);

    m_chooser.addOption("Test Auto", new TestAuto(swerveDrive));


